mapper.select:
SELECT 
FROM todo_items WHERE id IN

    #{item}

mapper.update:
UPDATE todo_items
SET list_id=#{listId}, content=#{content}, position=#{position}, is_completed=#{isCompleted}, created_time=#{createdTime}, updated_time=#{updatedTime}, deleted_time=#{deletedTime}, create_user=#{createUser}, update_user=#{updateUser}, delete_user=#{deleteUser}
WHERE id=#{id}

UPDATE todo_items
content = #{content}, title = #{title}, status = #{status},
WHERE id = #{id}

UPDATE todo_items
SET deleted_time = NOW(), delete_user = #{deletedUserId}
WHERE id = #{id}
