mapper.update:
UPDATE todo_lists
SET name=#{name}, status=#{status}, created_time=#{createdTime}, updated_time=#{updatedTime}, deleted_time=#{deletedTime}, create_user=#{createUser}, update_user=#{updateUser}, delete_user=#{deleteUser}
WHERE id=#{id}

UPDATE todo_lists
name = #{name}, status = #{status}, created_time = #{createdTime}, updated_time = #{updatedTime}, deleted_time = #{deletedTime}, create_user = #{createUser}, update_user = #{updateUser}, delete_user = #{deleteUser},
WHERE id = #{id}

UPDATE todo_lists
SET deleted_time = NOW(), delete_user = #{deletedUserId}
WHERE id = #{id}
